<script>
    /* Copyright (c) 2020 Jacques Desodt */
    import {formatTime} from '../scripts/dates.js';

    export let args = {};
    export let hour = true;
    export let selected = false;
    export let value = 0;

    /* Local variables */
    let am;
    $: am = value >= 0 && value < 12;
    let pmHour;
    $: pmHour = args.is24h ? value : ((value - 12) === 0 ? 12 : value - 12);
    let style;
    $: style = selected ? 'color:' + args.color + ';' : '';

</script>

<div class="_tp-value" style={style}
     on:click
>
    {formatTime(hour ? (am ? value : pmHour) : value)}
</div>

<style>._tp-value{color:hsla(0,0%,100%,.5);cursor:pointer;transition-duration:.3s;transition-property:background-color,border-color,color,fill,stroke}._tp-value:hover{background-color:rgba(0,0,0,.1)}</style>
